import React from 'react';
import 'antd/dist/antd.css';
import Clock from './Clock'
// import Navi from './Navi'
import Weather from './Weather'
import {SettingOutlined, CalendarOutlined, EllipsisOutlined,RadiusSettingOutlined} from '@ant-design/icons';
import { Menu } from 'antd';
import { Octrl } from './octrl';

const {SubMenu} = Menu;
class Mirror extends React.Component {
  state = {
    current: 'octrl',
  };
  handleClick = e => {
    // console.log('click ', e);
    this.setState({ current: e.key });
  };
  render() {
    const { current } = this.state;
    return (	
    //   <div style={{
    //     background: 'white',
    //     color: 'black',
    //     height: '100%',
    //     width: '100%',
    //     overflow: 'hidden'
    //   }}>

        /* <Weather />
        <Clock /> */
        
        
        
    //   </div>
	<div style={{
		width:"100%",
		height: "100%",
		backgroundColor: "black",
		color: "white"
	}}>
		<Menu onClick={this.handleClick} selectedKeys={[current]} mode="horizontal" theme="light" style={{backgroundColor:"black",color:"white",position:"absolute",top:"0%",left:"0%",width:"100%"}}>
			{/* <Menu.Item key="cal" icon={<CalendarOutlined />}>实验室预约</Menu.Item> */}
			<Menu.Item key="octrl" icon={<SettingOutlined />}>控制面板</Menu.Item>
      <Menu.Item key="ictrl" icon={<RadiusSettingOutlined />}>实验室控制</Menu.Item>
			<SubMenu key="more" icon={<EllipsisOutlined />}></SubMenu>
        </Menu>
		<Clock />
		<Weather />
		<div style={{
                position: 'absolute',
                top: '10%',
                left: '0',
                width: '100%',
                height: '80%'
        }}>
			<div style = {{
                    position: 'absolute',
                    top: '0',
                    left: '0',
                    width: '100%',
                    height: '100%'
                }}>
                    {
                        this.state.current == "screenSaver"?
                                (<div></div>)
                            :this.state.current == "octrl"?<Octrl />:<Octrl />         
                    }
        	</div>
		</div>
    
    <div  style={{
      position: 'absolute',
      left: '0',
      bottom: '0',
      width: '100%',
      height: '20%',
      backgroundImage: 'url("./background.png")',
      backgroundSize: '100% 100%'
    }}>
          <div style={{
          'position': 'absolute',
          'top': '45%',
          'left': '5%',
          'line-height': '0.5em',
          'font-size': '3em',
        }}>
          计算社会科学研究中心
    </div>
    </div>
	</div>
    )
  }
}

export default Mirror;
